const mongoose = require('mongoose')
const User = mongoose.model('User')
const Client = mongoose.model('Client')
const DocumentFact = mongoose.model('documentFact')
var pdfMake = require('pdfmake/build/pdfmake');
var pdfFonts = require('pdfmake/build/vfs_fonts');
const cloudinary = require('cloudinary');
const nodemailer = require ('nodemailer')
const sendGridTransport = require ('nodemailer-sendgrid-transport')

const transporter = nodemailer.createTransport(sendGridTransport({
    sendMail: true,
    auth: {
        api_key: process.env.apiKey
    }
}))


cloudinary.config({
    cloud_name: process.env.cloud_name,
    api_key: process.env.api_cloudinary_key,
    api_secret: process.env.api_cloudinary_secret
})

pdfMake.vfs = pdfFonts.pdfMake.vfs

/** get all fact documents */
exports.get_fact_documents = (req, res) => {
    DocumentFact.find()
    .populate("created_by", "nom prenom email")
    .then((result) => {
        res.send(result)
    })
    .catch((error) => {
        console.log(error)
    }) 
}

/** generate fact document */
exports.generate_document_fact = (req, res) => {
    const {type, description, devise, client_name} = req.body
    if (!type | !description, !devise, !client_name) {
        return res.status(422).json({error: "Please add all the fields"})
    }

    Client.find({client_name: client_name})
    .then((result) => {
        const client = result[0].client_name
        const client_adress = result[0].adresse
        const client_email = result[0].email
        const client_phone = result[0].numTelephone
        const client_rib = result[0].RIB

        const CreatedDoc = new DocumentFact({
            type,
            description,
            devise,
            client_name : client,
            date_creation: new Date(),
            created_by: req.user
        })
        CreatedDoc.save()
        .then((createdDoc) => {
            const date = createdDoc.date_creation
            const id = createdDoc._id
            if (type == "Devis") {
                const devis = {
                    header: {
                        margin: [30, 10, 30, 20],
                        columns: [
                            {
                                // usually you would use a dataUri instead of the name for client-side printing
                                // sampleImage.jpg however works inside playground so you can play with it
                                image: 'data:image/png;base64,',
                                width: 120
                            },
                            {
                                margin: [50, 15, 40, 0],
                                stack: [
                                    {text: date},
                                ],
                                alignment: 'right'
                            }
                        ]
                    },
                    content: [
                        {
                            text: 'Devis',
                            style: 'header',
                            alignment: 'center',
                            margin: [0, 150, 0, 60]
                        },
                        ,
                        {
                            stack: [
                                {text: [
                                    `devis pour  ${client} ${client_adress} ${client_email} ${client_phone} ${client_rib}  `,               
                                    `au ${date} `,
                                ]
                            },
                            { text: 
                                []},
                            
    
                            `Fait le : ${date}`,
                            `Signature :`,
    
    
                            ],
                            style: 'superMargin'
                        },
                        
                    ],
                    styles: {
                        header: {
                            fontSize: 18,
                            bold: true,
                            alignment:'justify',
                            
                        },
                        subheader: {
                            fontSize: 15,
                            bold: true
                        },
                        quote: {
                            italics: true
                        },
                        small: {
                            fontSize: 8
                        },
                        superMargin: {
                            margin: [20, 0, 40, 0],
                            fontSize: 12
                        },
                        adresse: {
    
                        }
                    }
                }
                const pdfDoc = pdfMake.createPdf(devis)
                pdfDoc.getBase64((data) => {
                    const download = Buffer.from(data.toString('utf-8'), 'base64')
                    cloudinary.uploader.upload_stream((uploadedDoc) => {
                        const Document = uploadedDoc.secure_url
                        const updates = { documentLink: Document, withLink: req.body.withLink}
                        const options = {new:true}
                        DocumentFact.findByIdAndUpdate(id, updates, options)
                        .populate("asked_by", "nom prenom email")
                        .then((updatedDoc) => {
                            if (req.body.withEmail == true){
                                const emailData = {
                                    to: client_email, 
                                    from: "yosra.sahnoun@esprit.tn",
                                    subject: "Devis",
                                    html: `
                                    <p>Vous avez demandé un devis</p>
                                    <h5>hello</h5>
                                    `,
                                    attachments: [{
                                        filename: 'file.pdf',
                                        content: new Buffer.from(download, 'utf-8'),
                                        contentType: 'application/pdf'
                                      }]
                                }
                                transporter.sendMail(emailData)
                                .then((res)=> {
                                    console.log(res)
                                }).catch((err) => {
                                    console.log(err)
                                })
                            }
                            res.json(updatedDoc)                        
                        })
                        .catch((error) => {
                            console.log(error)
                        })
                    }).end(download)
                }) 
            } else if (type == "Facture") {
                const facture = {
                    header: {
                        margin: [30, 10, 30, 20],
                        columns: [
                            {
                                // usually you would use a dataUri instead of the name for client-side printing
                                // sampleImage.jpg however works inside playground so you can play with it
                                image: 'data:image/png;base64,',
                                width: 120
                            },
                            {
                                margin: [50, 15, 40, 0],
                                stack: [
                                    {text: date},
                                ],
                                alignment: 'right'
                            }
                        ]
                    },
                    content: [
                        {
                            text: 'Facture',
                            style: 'header',
                            alignment: 'center',
                            margin: [0, 150, 0, 60]
                        },
                        ,
                        {
                            stack: [
                                {text: [
                                    `devis pour  ${client} ${client_adress} ${client_email} ${client_phone} ${client_rib}  `,               
                                    `au ${date} `,
                                ]
                            },
                            { text: 
                                []},
                            
    
                            `Fait le : ${date}`,
                            `Signature :`,
    
    
                            ],
                            style: 'superMargin'
                        },
                        
                    ],
                    styles: {
                        header: {
                            fontSize: 18,
                            bold: true,
                            alignment:'justify',
                            
                        },
                        subheader: {
                            fontSize: 15,
                            bold: true
                        },
                        quote: {
                            italics: true
                        },
                        small: {
                            fontSize: 8
                        },
                        superMargin: {
                            margin: [20, 0, 40, 0],
                            fontSize: 12
                        },
                        adresse: {
    
                        }
                    }
                }
                const pdfDoc = pdfMake.createPdf(facture)
                pdfDoc.getBase64((data) => {
                    const download = Buffer.from(data.toString('utf-8'), 'base64')
                    cloudinary.uploader.upload_stream((uploadedDoc) => {
                        const Document = uploadedDoc.secure_url
                        const updates = { documentLink: Document, withLink: req.body.withLink}
                        const options = {new:true}
                        DocumentFact.findByIdAndUpdate(id, updates, options)
                        .populate("asked_by", "nom prenom email")
                        .then((updatedDoc) => {
                            if (req.body.withEmail == true){
                                const emailData = {
                                    to: client_email, 
                                    from: "yosra.sahnoun@esprit.tn",
                                    subject: "Facture",
                                    html: `
                                    <p>Vous avez demandé une facture</p>
                                    <h5>hello</h5>
                                    `,
                                    attachments: [{
                                        filename: 'facture.pdf',
                                        content: new Buffer.from(download, 'utf-8'),
                                        contentType: 'application/pdf'
                                      }]
                                }
                                transporter.sendMail(emailData)
                                .then((res)=> {
                                    console.log(res)
                                }).catch((err) => {
                                    console.log(err)
                                })
                            }
                            res.json(updatedDoc)                        
                        })
                        .catch((error) => {
                            console.log(error)
                        })
                    }).end(download)
                })
            }

        })
        .catch((error)=> {
            console.log(error)
        })

    })
    .catch((error) => {
        console.log(error)
    })
}

/** delete fact document */
exports.delete_fact_document = (req, res) => {
    const id = mongoose.Types.ObjectId(req.params.id)
    DocumentFact.findByIdAndDelete(id)
    .then((deletedUser) => {
        res.send(deletedUser)
    })
    .catch((error) => {
        console.log(error)
    })
}

